mapper.select:
SELECT * FROM api_management

    1=1

        AND (api_name LIKE CONCAT('%', #{keyword}, '%') OR api_path LIKE CONCAT('%', #{keyword}, '%'))


        AND project_id = #{projectId}


ORDER BY api_id
LIMIT #{pageSize} OFFSET #{offset}

SELECT * FROM api_management

    item_type = #{itemType}

        AND project_id = #{projectId}


ORDER BY api_id

SELECT * FROM api_management


        parent_id = #{parentId}


        parent_id IS NULL


        AND project_id = #{projectId}


ORDER BY item_type, api_id

