SELECT COUNT(*) FROM test_case

    1=1

        AND case_status = #{caseStatus}


        AND (case_title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND project_id = #{projectId}

SELECT * FROM test_case

    1=1

        AND case_status = #{caseStatus}


        AND (case_title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND project_id = #{projectId}


ORDER BY case_id
LIMIT #{pageSize} OFFSET #{offset}
