mapper.update:
UPDATE ui_test_config
config_name = #{configName}, config_type = #{configType}, browser_type = #{browserType}, browser_version = #{browserVersion}, device_type = #{deviceType}, device_name = #{deviceName}, platform_name = #{platformName}, platform_version = #{platformVersion}, default_wait_timeout = #{defaultWaitTimeout}, page_load_timeout = #{pageLoadTimeout}, script_timeout = #{scriptTimeout}, screenshot_policy = #{screenshotPolicy}, retry_count = #{retryCount}, retry_interval = #{retryInterval}, headless = #{headless}, proxy_settings = #{proxySettings}, capabilities = #{capabilities}, is_default = #{isDefault}, is_active = #{isActive}, updated_by = #{updatedBy}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM ui_test_config
WHERE config_type = #{configType}

mapper.update:
UPDATE ui_test_config
SET is_default = 0
WHERE config_type = #{configType}
