mapper.select:
SELECT FROM ui_test_suite
AND (suite_name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY created_at DESC LIMIT #{offset}, #{pageSize}

mapper.insert:
INSERT INTO ui_test_suite (
    suite_name, project_id, description, execute_in_parallel, max_parallel_count, 
    created_by, created_at
) VALUES (
    #{suiteName}, #{projectId}, #{description}, #{executeInParallel}, #{maxParallelCount}, 
    #{createdBy}, #{createdAt}
)

mapper.update:
UPDATE ui_test_suite
suite_name = #{suiteName}, project_id = #{projectId}, description = #{description}, execute_in_parallel = #{executeInParallel}, max_parallel_count = #{maxParallelCount}, updated_at = #{updatedAt},
WHERE id = #{id}
